let showDetail = (detailId, buttonId) => {
  // showdetail
  //   document.getElementById(detailId).style.display = "block";
  document.getElementById(detailId).classList.add("section_1_detail_active");
  // hide button
  document.getElementById(buttonId).style.display = "none";
  var ele = document.getElementById("section_1");
  window.scrollTo(ele.offsetLeft, ele.offsetTop + 800);
};

let hideDetail = (detailId, buttonId, sectionId) => {
  // showdetail
  document.getElementById(detailId).style.display = "none";
  // hide button
  document.getElementById(buttonId).style.display = "block";
  //scroll to model
  var ele = document.getElementById(sectionId);
  window.scrollTo(ele.offsetLeft, ele.offsetTop);
};
